import React, { useState, useRef, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import { useLocation } from '../contexts/LocationContext';
import { toast } from 'react-toastify';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const InteractiveMap = ({ isPanelExpanded, setIsPanelExpanded, showOnLanding = false }) => {
  const {
    userLocation,
    selectedDeliverySpot,
    deliverySpots,
    selectDeliverySpot,
    confirmDeliverySpot,
    searchAddresses,
    selectedAddress,
    updateAddress,
    isSearching,
    searchResults
  } = useLocation();

  const [map, setMap] = useState(null);
  const [searchQuery, setSearchQuery] = useState(selectedAddress || '');
  const [showMapSuggestions, setShowMapSuggestions] = useState(false);
  const mapRef = useRef();

  // Update search query when selectedAddress changes
  useEffect(() => {
    setSearchQuery(selectedAddress || '');
  }, [selectedAddress]);

  // Use props or default state
  const [localPanelExpanded, setLocalPanelExpanded] = useState(false);
  const panelExpanded = isPanelExpanded !== undefined ? isPanelExpanded : localPanelExpanded;
  const setPanelExpanded = setIsPanelExpanded || setLocalPanelExpanded;

  // Default center (Andheri West coordinates)
  const defaultCenter = [19.1197, 72.8464]; // Andheri Metro Station
  const center = userLocation || defaultCenter;

  // Custom marker icons
  const userIcon = new L.DivIcon({
    className: 'user-marker',
    iconSize: [16, 16],
    iconAnchor: [8, 8]
  });

  const createDeliveryIcon = (spot) => {
    const isSelected = selectedDeliverySpot?.id === spot.id;
    return new L.DivIcon({
      className: `delivery-marker ${isSelected ? 'selected' : ''}`,
      iconSize: [32, 40],
      iconAnchor: [16, 40],
      popupAnchor: [0, -40],
      html: `<div style="width: 32px; height: 40px; display: flex; align-items: center; justify-content: center; font-size: 16px; background: ${isSelected ? 'var(--brand)' : 'white'}; color: ${isSelected ? 'white' : 'var(--ink)'}; border-radius: 20px 20px 20px 4px; border: 2px solid var(--brand);">${getSpotIcon(spot.type)}</div>`
    });
  };

  const getSpotIcon = (type) => {
    const icons = {
      'main_entrance': 'ðŸš‡',
      'shopping': 'ðŸ›ï¸',
      'residential': 'ðŸ ',
      'recreational': 'ðŸ–ï¸',
      'industrial': 'ðŸ­'
    };
    return icons[type] || 'ðŸ“';
  };

  const handleSpotSelect = (spotId) => {
    const success = selectDeliverySpot(spotId);
    if (success) {
      toast.success(`Selected: ${deliverySpots.find(s => s.id === spotId)?.name}`);
      if (map) {
        const spot = deliverySpots.find(s => s.id === spotId);
        if (spot) {
          map.setView(spot.coordinates, 18, { animate: true, duration: 0.5 });
        }
      }
    }
  };

  const handleConfirmSpot = () => {
    const success = confirmDeliverySpot();
    if (success) {
      setPanelExpanded(false);
    }
  };

  const handleMapSearch = async (value) => {
    setSearchQuery(value);
    if (value.length > 2) {
      try {
        await searchAddresses(value);
        setShowMapSuggestions(true);
      } catch (error) {
        console.error('Map search error:', error);
        setShowMapSuggestions(false);
      }
    } else {
      setShowMapSuggestions(false);
    }
  };

  const selectMapResult = (result) => {
    const address = typeof result === 'string' ? result : result.formatted_address;
    updateAddress(address);
    setSearchQuery(address);
    setShowMapSuggestions(false);

    // Center map on selected location if coordinates available
    if (result.geometry && map) {
      const { lat, lng } = result.geometry.location;
      map.setView([lat, lng], 16, { animate: true, duration: 0.5 });
    }
  };

  const filteredSpots = deliverySpots.filter(spot => {
    return spot.available;
  });

  // Map event handler component
  const MapEvents = () => {
    useMapEvents({
      ready: (e) => {
        if (e && e.target) {
          setMap(e.target);
        }
      },
    });
    return null;
  };

  return (
    <div className="map-section">
      <h4 style={{ fontWeight: 600, margin: '0 0 16px 0', textAlign: 'center' }}>
        Select Your Precise Delivery Location
      </h4>

      <div className="map-container">
        <div className="map-overlay">
          <div style={{ position: 'relative' }}>
            <input 
              type="text" 
              className="map-search-bar" 
              placeholder="Search for a specific location in Andheri..."
              value={searchQuery}
              onChange={(e) => handleMapSearch(e.target.value)}
              onBlur={() => setTimeout(() => setShowMapSuggestions(false), 200)}
            />

            {/* Map Search Suggestions */}
            {showMapSuggestions && (
              <div style={{
                position: 'absolute',
                top: '100%',
                left: 0,
                right: 0,
                background: 'white',
                borderRadius: '12px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
                maxHeight: '200px',
                overflowY: 'auto',
                zIndex: 1010,
                marginTop: '4px'
              }}>
                {isSearching && (
                  <div style={{ 
                    padding: '12px 16px', 
                    display: 'flex', 
                    alignItems: 'center',
                    opacity: 0.7
                  }}>
                    <div className="spinner" style={{ width: '16px', height: '16px', marginRight: '8px' }}></div>
                    <span style={{ fontSize: '0.875rem' }}>Searching...</span>
                  </div>
                )}
                {!isSearching && searchResults.map((result, index) => (
                  <div 
                    key={result.place_id || index}
                    style={{
                      padding: '12px 16px',
                      borderBottom: '1px solid #f1f5f9',
                      cursor: 'pointer',
                      fontSize: '0.875rem',
                      transition: 'background-color 0.2s ease'
                    }}
                    onClick={() => selectMapResult(result)}
                    onMouseEnter={(e) => e.target.style.background = 'var(--brand-light)'}
                    onMouseLeave={(e) => e.target.style.background = 'transparent'}
                  >
                    <div style={{ fontWeight: 600, color: 'var(--ink)' }}>
                      {result.formatted_address.split(',')[0]}
                    </div>
                    <div style={{ fontSize: '0.75rem', color: 'var(--muted)' }}>
                      {result.formatted_address}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <MapContainer
          center={center}
          zoom={15}
          style={{ height: '100%', width: '100%', borderRadius: '14px' }}
          ref={mapRef}
        >
          <MapEvents />
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* User location marker */}
          {userLocation && (
            <Marker 
              position={userLocation} 
              icon={userIcon}
            />
          )}

          {/* Delivery spot markers */}
          {filteredSpots.map((spot) => (
            <Marker
              key={`${spot.id}-${selectedDeliverySpot?.id === spot.id ? 'selected' : 'unselected'}`}
              position={spot.coordinates}
              icon={createDeliveryIcon(spot)}
              eventHandlers={{
                click: () => handleSpotSelect(spot.id)
              }}
            />
          ))}
        </MapContainer>

        {/* Delivery Spots Panel */}
        <div className={`delivery-spots-panel ${panelExpanded ? 'expanded' : ''}`}>
          <div className="panel-header" onClick={() => setPanelExpanded(!panelExpanded)}>
            <div style={{ textAlign: 'center', width: '100%' }}>
              <div className="panel-handle"></div>
              <h4 className="panel-title">Available Delivery Spots</h4>
            </div>
          </div>

          <div>
            {filteredSpots.map((spot) => (
              <div 
                key={spot.id}
                className={`delivery-spot ${selectedDeliverySpot?.id === spot.id ? 'selected' : ''}`}
                onClick={() => handleSpotSelect(spot.id)}
              >
                <div className="spot-icon">{getSpotIcon(spot.type)}</div>
                <div className="spot-info">
                  <h5 className="spot-name">{spot.name}</h5>
                  <div className="spot-details">
                    <span>{spot.address}</span>
                    <span className="spot-distance">{spot.distance}</span>
                    <span>{spot.walkTime}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button 
            className={`confirm-spot-btn ${selectedDeliverySpot ? 'active' : ''}`}
            onClick={handleConfirmSpot}
            disabled={!selectedDeliverySpot}
          >
            {selectedDeliverySpot ? `Confirm ${selectedDeliverySpot.name}` : 'Select a delivery spot'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default InteractiveMap;
