import React, { useState, useEffect } from 'react';
import { useCart } from '../contexts/CartContext';
import { productCatalog, formatCurrency } from '../data/products';
import BottomNavigation from './BottomNavigation';

const OrdersPage = () => {
  const { getCartTotal } = useCart();
  const [etaMinutes, setEtaMinutes] = useState(8);
  const [orderStatus, setOrderStatus] = useState({
    confirmed: true,
    packed: true,
    dispatched: true,
    arriving: false
  });

  // Scroll to top when component mounts
  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, []);

  // Mock order data - in production, this would come from API
  const orderData = getCartTotal(productCatalog);

  useEffect(() => {
    // Simulate delivery tracking
    const interval = setInterval(() => {
      setEtaMinutes(prev => {
        const newTime = Math.max(0, prev - 1);

        // Update status based on time remaining
        if (newTime <= 7 && !orderStatus.confirmed) {
          setOrderStatus(prev => ({ ...prev, confirmed: true }));
        }
        if (newTime <= 5 && !orderStatus.packed) {
          setOrderStatus(prev => ({ ...prev, packed: true }));
        }
        if (newTime <= 3 && !orderStatus.dispatched) {
          setOrderStatus(prev => ({ ...prev, dispatched: true }));
        }
        if (newTime === 0) {
          setOrderStatus(prev => ({ ...prev, arriving: true }));
        }

        return newTime;
      });
    }, 3000); // Update every 3 seconds for demo

    return () => clearInterval(interval);
  }, [orderStatus]);

  const statusSteps = [
    { 
      id: 'confirmed', 
      label: 'Order Confirmed', 
      completed: orderStatus.confirmed,
      icon: '✓'
    },
    { 
      id: 'packed', 
      label: 'Items Packed', 
      completed: orderStatus.packed,
      icon: '✓'
    },
    { 
      id: 'dispatched', 
      label: 'Drone Dispatched', 
      completed: orderStatus.dispatched,
      icon: '✓'
    },
    { 
      id: 'arriving', 
      label: 'Arriving Soon', 
      completed: orderStatus.arriving,
      icon: '4'
    }
  ];

  return (
    <div className="screen active">
      {/* Tracking Header */}
      <div className="tracking-header">
        <p className="eta-label">Your order is arriving in</p>
        <h1 className="eta-display">
          <span>{etaMinutes}</span> min
        </h1>
        <div className="status-indicator">🛩️ Live Tracking</div>
      </div>

      {/* Status Timeline */}
      <div className="status-timeline">
        {statusSteps.map((step, index) => (
          <div 
            key={step.id}
            className={`status-step ${step.completed ? 'completed' : ''}`}
          >
            <div className="step-indicator">
              {step.completed ? '✓' : index + 1}
            </div>
            <div className="step-label">{step.label}</div>
          </div>
        ))}
      </div>

      {/* Enhanced Map */}
      <div className="tracking-map">
        <div className="delivery-route">
          <div className="route-waypoint start"></div>
          <div className="route-waypoint end"></div>
        </div>

        {/* Animated Drone */}
        <div className="animated-drone">
          <svg viewBox="0 0 100 100" aria-hidden="true">
            <g>
              <rect x="30" y="35" width="40" height="20" rx="10" fill="var(--brand)"/>
              <rect x="20" y="25" width="60" height="8" rx="4" fill="var(--brand-2)"/>
              <circle cx="20" cy="29" r="15" fill="rgba(255,255,255,0.8)" stroke="var(--brand-2)" strokeWidth="2"/>
              <circle cx="80" cy="29" r="15" fill="rgba(255,255,255,0.8)" stroke="var(--brand-2)" strokeWidth="2"/>
              <rect x="45" y="55" width="10" height="12" rx="2" fill="#374151"/>
            </g>
          </svg>
        </div>
      </div>

      {/* Pilot Information */}
      <div className="pilot-card">
        <div className="pilot-avatar">👨‍✈️</div>
        <div className="pilot-info">
          <h4>Pilot: Samir Kumar (DG-47)</h4>
          <p className="pilot-details">1.2 km away • Contactless delivery • 4.9★ rating</p>
        </div>
        <button className="contact-btn">📞 Call</button>
      </div>

      {/* Order Summary */}
      <div className="bill-summary">
        <h3 style={{ margin: '0 0 16px 0' }}>Order Summary</h3>
        <div className="bill-row">
          <span>Items (3)</span>
          <span>{formatCurrency(orderData.subtotal || 553)}</span>
        </div>
        <div className="bill-row">
          <span>Drone convenience fee</span>
          <span>{formatCurrency(19)}</span>
        </div>
        <div className="bill-row">
          <span>Taxes & fees</span>
          <span>{formatCurrency(orderData.tax || 28)}</span>
        </div>
        <div className="bill-row" style={{ color: 'var(--success)' }}>
          <span>Delivery</span>
          <strong>FREE</strong>
        </div>
        <div className="bill-row bill-total">
          <span>Total Paid</span>
          <span>{formatCurrency(orderData.total || 600)}</span>
        </div>
      </div>

      {/* Bottom Navigation */}
      <BottomNavigation activeScreen="orders" />
    </div>
  );
};

export default OrdersPage;
